const ActionButtons = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: theme.spacing(2),
  left: '50%',
  transform: 'translateX(-50%)',
  display: 'flex',
  justifyContent: 'center',
  gap: theme.spacing(3),
  zIndex: 3,
  width: 'auto',
  padding: theme.spacing(2),
}));

const ActionButton = styled(IconButton)(({ theme, color }) => ({
  width: 54,
  height: 54,
  backgroundColor: 'white',
  boxShadow: '0 2px 6px rgba(0, 0, 0, 0.15)',
  '&:hover': {
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    transform: 'scale(1.1)',
  },
  transition: 'all 0.2s ease',
}));

const ActionIndicator = styled(Box)(({ type }) => ({
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%) scale(0)',
  padding: '16px 32px',
  borderRadius: '12px',
  fontSize: '38px',
  fontWeight: 'bold',
  textTransform: 'uppercase',
  backdropFilter: 'blur(8px)',
  willChange: 'transform, opacity',
  zIndex: 10,
  animation: 'popIn 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards',
  '@keyframes popIn': {
    '0%': {
      transform: 'translate(-50%, -50%) scale(0)',
      opacity: 0,
    },
    '100%': {
      transform: 'translate(-50%, -50%) scale(1)',
      opacity: 1,
    }
  },
  ...(type === 'like' ? {
    color: '#4CAF50',
    backgroundColor: 'rgba(76, 175, 80, 0.2)',
    border: '4px solid rgba(76, 175, 80, 0.8)',
    textShadow: '0 0 10px rgba(76, 175, 80, 0.4)',
  } : type === 'superlike' ? {
    color: '#00bcd4',
    backgroundColor: 'rgba(0, 188, 212, 0.2)',
    border: '4px solid rgba(0, 188, 212, 0.8)',
    textShadow: '0 0 10px rgba(0, 188, 212, 0.4)',
  } : {
    color: '#f44336',
    backgroundColor: 'rgba(244, 67, 54, 0.2)',
    border: '4px solid rgba(244, 67, 54, 0.8)',
    textShadow: '0 0 10px rgba(244, 67, 54, 0.4)',
  }),
}));

const SparkleContainer = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  pointerEvents: 'none',
  zIndex: 2,
});

const Star = styled(Box)(({ delay, size, color }) => ({
  position: 'absolute',
  width: size + 'px',
  height: size + 'px',
  backgroundColor: color,
  borderRadius: '50%',
  animation: `sparkle 0.5s linear ${delay}s forwards`,
  opacity: 0,
  '@keyframes sparkle': {
    '0%': {
      transform: 'scale(0) rotate(0deg)',
      opacity: 0,
    },
    '50%': {
      opacity: 1,
    },
    '100%': {
      transform: 'scale(1) rotate(180deg)',
      opacity: 0,
    },
  },
}));

const PhotoNavigation = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  height: '75%',
  display: 'flex',
});

const PhotoNavArea = styled(Box)({
  flex: 1,
  cursor: 'pointer',
});

const PhotoIndicators = styled(Box)(({ theme }) => ({
