  width: '100%',
  height: '520px',
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius * 2,
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
  position: 'relative',
  overflow: 'hidden',
}));

const CardOverlay = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: 0,
  left: 0,
  right: 0,
  padding: '20px',
  background: 'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.7) 60%, transparent 100%)',
  color: 'white',
  maxHeight: '70%',
  overflowY: 'auto',
  transition: 'max-height 0.3s ease',
  scrollbarWidth: 'thin',
  scrollbarColor: 'rgba(255, 255, 255, 0.3) transparent',
  '&::-webkit-scrollbar': {
    width: '6px',
  },
  '&::-webkit-scrollbar-track': {
    background: 'transparent',
  },
  '&::-webkit-scrollbar-thumb': {
    background: 'rgba(255, 255, 255, 0.3)',
    borderRadius: '3px',
  },
  '&:hover': {
    maxHeight: '85%',
  }
}));

const ActionButtons = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: '-70px',
  left: 0,
  right: 0,
  display: 'flex',
  justifyContent: 'center',
  gap: theme.spacing(4),
  width: '100%',
  zIndex: 1,
}));

const ActionButton = styled(IconButton)(({ theme, color }) => ({
  backgroundColor: color === 'like' 
    ? theme.palette.success.main 
    : color === 'superlike' 
      ? '#00bcd4' 
      : theme.palette.error.main,
  width: '60px',
  height: '60px',
  boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)',
  '&:hover': {
    backgroundColor: color === 'like' 
      ? theme.palette.success.light 
      : color === 'superlike' 
        ? '#26c6da' 
        : theme.palette.error.light,
    transform: 'scale(1.1)',
  },
  '& svg': {
    color: 'white',
    fontSize: '2rem',
  },
}));

const ActionIndicator = styled(Box)(({ type }) => ({
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%) scale(0)',
  padding: '16px 32px',
  borderRadius: '12px',
  fontSize: '38px',
  fontWeight: 'bold',
  textTransform: 'uppercase',
  backdropFilter: 'blur(8px)',
  willChange: 'transform, opacity',
  zIndex: 10,
  animation: 'popIn 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards',
  '@keyframes popIn': {
    '0%': {
      transform: 'translate(-50%, -50%) scale(0)',
      opacity: 0,
    },
    '100%': {
      transform: 'translate(-50%, -50%) scale(1)',
      opacity: 1,
    }
  },
  ...(type === 'like' ? {
    color: '#4CAF50',
    backgroundColor: 'rgba(76, 175, 80, 0.2)',
    border: '4px solid rgba(76, 175, 80, 0.8)',
    textShadow: '0 0 10px rgba(76, 175, 80, 0.4)',
  } : type === 'superlike' ? {
    color: '#00bcd4',
    backgroundColor: 'rgba(0, 188, 212, 0.2)',
    border: '4px solid rgba(0, 188, 212, 0.8)',
    textShadow: '0 0 10px rgba(0, 188, 212, 0.4)',
  } : {
    color: '#f44336',
    backgroundColor: 'rgba(244, 67, 54, 0.2)',
    border: '4px solid rgba(244, 67, 54, 0.8)',
    textShadow: '0 0 10px rgba(244, 67, 54, 0.4)',
  }),
}));

const Star = styled(Box)(({ delay, top, left, size, color }) => ({
  position: 'absolute',
  width: 0,
  height: 0,
  border: `${size}px solid transparent`,
  borderBottom: `${size * 1.4}px solid ${color}`,
  opacity: 0,
  transform: 'rotate(0deg)',
  willChange: 'transform, opacity',
  '&:after': {
    content: '""',
    position: 'absolute',
    left: `-${size}px`,
    top: `${size * 1.4}px`,
    width: 0,
    height: 0,
    border: `${size}px solid transparent`,
    borderTop: `${size * 1.4}px solid ${color}`,
  },
  animation: `twinkle 1s ${delay}s ease-out`,
  '@keyframes twinkle': {
    '0%': {
      transform: 'rotate(0deg) scale(0)',
      opacity: 0,
    },
    '50%': {
      transform: 'rotate(180deg) scale(1)',
      opacity: 1,
    },
    '100%': {
      transform: 'rotate(360deg) scale(0)',
      opacity: 0,
    }
  },
  top,
  left,
}));

const SparkleContainer = styled(Box)({
  position: 'absolute',
  top: '50%',
  left: '50%',
  width: '260px',
  height: '260px',
  transform: 'translate(-50%, -50%)',
  pointerEvents: 'none',
  zIndex: 9,
});

const PhotoNavigation = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  height: '75%',
  display: 'flex',
});

const PhotoNavArea = styled(Box)({
  flex: 1,
  cursor: 'pointer',
});

const PhotoIndicators = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: theme.spacing(2),
  left: 0,
  right: 0,
  display: 'flex',
  justifyContent: 'center',
  gap: theme.spacing(1),
  padding: theme.spacing(0, 2),
  zIndex: 2,
}));

const PhotoIndicator = styled(Box)(({ active, theme }) => ({
  height: 4,
  flex: 1,
  maxWidth: 60,
  backgroundColor: active ? 'white' : 'rgba(255, 255, 255, 0.5)',
  borderRadius: 2,
  transition: 'background-color 0.3s ease',
}));

const generateStars = (count, type) => {
  const getColor = () => {
    switch(type) {
      case 'like':
        return 'rgba(76, 175, 80, 0.8)';
      case 'superlike':
        return 'rgba(0, 188, 212, 0.8)';
      default:
        return 'rgba(244, 67, 54, 0.8)';
    }
  };
  
  return Array.from({ length: count }, (_, i) => {
    const angle = (360 / count) * i;
    const radius = 80 + (i % 2) * 20;
    const top = 130 + radius * Math.sin((angle * Math.PI) / 180);
    const left = 130 + radius * Math.cos((angle * Math.PI) / 180);
    return {
      id: i,
      delay: i * 0.05,
      top: `${top}px`,
      left: `${left}px`,
      size: 2 + (i % 3),
      color: getColor(),
    };
  });
};

const SwipeCard = ({ user, onLike, onPass, onSuperLike }) => {
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const [action, setAction] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [stars, setStars] = useState([]);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const startPos = useRef({ x: 0, y: 0 });
  const cardRef = useRef(null);

  const handlePhotoNavigation = (e) => {
    if (isAnimating || isDragging) return;
    
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const width = rect.width;

    if (x < width / 2 && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(prev => prev - 1);
    } else if (x >= width / 2 && currentPhotoIndex < user.profile.photos.length - 1) {
      setCurrentPhotoIndex(prev => prev + 1);
    }
  };

  const handleTouchStart = (e) => {
    if (isAnimating) return;
    setIsDragging(true);
    const touch = e.touches[0];
    startPos.current = { x: touch.clientX, y: touch.clientY };
  };

  const handleMouseDown = (e) => {
    if (isAnimating) return;
    setIsDragging(true);
    startPos.current = { x: e.clientX, y: e.clientY };
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const touch = e.touches[0];
    const deltaX = touch.clientX - startPos.current.x;
    const deltaY = touch.clientY - startPos.current.y;
    handleMove(deltaX, deltaY);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const deltaX = e.clientX - startPos.current.x;
    const deltaY = e.clientY - startPos.current.y;
    handleMove(deltaX, deltaY);
  };

  const handleMove = (deltaX, deltaY) => {
    // Limit the vertical movement more than horizontal
    const limitedDeltaY = deltaY * 0.3;
    setPosition({ x: deltaX, y: limitedDeltaY });
    
    // Calculate swipe progress percentage with smoother threshold
    const progress = Math.abs(deltaX) / (cardRef.current?.offsetWidth || 300);
    const opacity = Math.min(progress * 1.5, 1); // Slower fade in
    
    // Update action based on direction and progress with relaxed thresholds
    if (Math.abs(deltaY) > 60 && deltaY < -60 && Math.abs(deltaX) < 60) {
      // Upward swipe - Superlike
      setAction('superlike');
    } else if (Math.abs(deltaX) > 30) { // Increased threshold for smoother transition
      // Horizontal swipe - Like/Nope
      setAction(deltaX > 0 ? 'like' : 'nope');
    } else {
      setAction(null);
    }
  };

  const handleSwipeEnd = () => {
    if (!isDragging) return;
    setIsDragging(false);

    const deltaX = position.x;
    const deltaY = position.y;
    const threshold = cardRef.current?.offsetWidth * 0.4 || 120; // 40% of card width

    if (Math.abs(deltaY) > 100 && deltaY < -100 && Math.abs(deltaX) < 50) {
      // Complete superlike action
      completeSwipe('superlike');
    } else if (Math.abs(deltaX) > threshold) {
      // Complete like/nope action
      completeSwipe(deltaX > 0 ? 'like' : 'nope');
    } else {
      // Reset position with spring animation
      setPosition({ x: 0, y: 0 });
      setAction(null);
    }
  };

  const completeSwipe = (type) => {
    const cardWidth = cardRef.current?.offsetWidth || 300;
    const finalX = type === 'like' ? cardWidth * 1.2 : 
                  type === 'nope' ? -cardWidth * 1.2 : 0;
    const finalY = type === 'superlike' ? -cardWidth * 1.2 : 0;

    setPosition({ x: finalX, y: finalY });
    handleAction(type, true);
  };

  const handleAction = (type, isSwipe = false) => {
    if (isAnimating) return;
    
    setIsAnimating(true);
    setAction(type);
    setStars(generateStars(12, type));

    if (!isSwipe) {
      const cardWidth = cardRef.current?.offsetWidth || 300;
      setPosition({ 
        x: type === 'like' ? cardWidth * 1.2 : 
           type === 'nope' ? -cardWidth * 1.2 : 0,
        y: type === 'superlike' ? -cardWidth * 1.2 : 0 
      });
    }

    setTimeout(async () => {
      try {
        if (type === 'like') {
          await onLike();
        } else if (type === 'superlike') {
          await onSuperLike();
        } else {
          onPass();
        }
      } catch (error) {
        console.error('Action failed:', error);
      }
      
      setAction(null);
      setIsAnimating(false);
      setStars([]);
      setPosition({ x: 0, y: 0 });
    }, 500); // Increased reset time for smoother transition
  };

  const getSwipeRotation = () => {
    if (!isDragging && !isAnimating) return 0;
    
    // Calculate rotation based on horizontal movement and vertical position
    const rotationFactor = 12; // Reduced max rotation for smoother feel
    const horizontalProgress = position.x / (cardRef.current?.offsetWidth || 300);
    const verticalInfluence = (position.y / 600) * -1; // Reduced vertical influence
    
    return horizontalProgress * rotationFactor * (1 + verticalInfluence);
  };

  return (
    <Box sx={{ position: 'relative', width: '100%', height: '100%' }}>
      <CardWrapper 
        ref={cardRef}
        sx={{ 
          transition: isDragging ? 'none' : 'all 0.5s cubic-bezier(0.23, 1, 0.32, 1)', // Slower, smoother transition
          transform: `
            translate(${position.x}px, ${position.y}px) 
            rotate(${getSwipeRotation()}deg) 
            scale(${action ? 0.98 : 1})
          `,
          opacity: action ? 0.8 : 1, // Increased opacity for softer fade
          filter: action ? 'blur(1px)' : 'none',
          willChange: 'transform, opacity, filter',
          cursor: isDragging ? 'grabbing' : 'grab',
          '&:hover': {
            transform: !isDragging && !isAnimating ? 'scale(1.01)' : undefined, // Reduced hover scale
          },
          '&:active': {
            transform: !isDragging && !isAnimating ? 'scale(0.99)' : undefined, // Reduced active scale
          },
        }}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleSwipeEnd}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleSwipeEnd}
        onMouseLeave={handleSwipeEnd}
      >
        <PhotoNavigation onClick={handlePhotoNavigation}>
          <PhotoNavArea />
        </PhotoNavigation>

        <PhotoIndicators>
          {user.profile.photos.map((_, index) => (
            <PhotoIndicator
              key={index}
              active={index === currentPhotoIndex}
            />
          ))}
        </PhotoIndicators>

        <CardMedia
          component="img"
          height="100%"
          image={user.profile.photos[currentPhotoIndex]}
          alt={`${user.profile.firstName}'s photo`}
          sx={{ 
            objectFit: 'cover',
            filter: action ? 'brightness(0.8)' : 'none',
            pointerEvents: 'none',
          }}
        />

        <CardOverlay>
          <Typography variant="h5" component="div" sx={{ mb: 1 }}>
            {user.profile.firstName}, {user.profile.age}
          </Typography>
          
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <LocationIcon sx={{ mr: 1, fontSize: '1.2rem' }} />
            <Typography variant="body2">
              {user.profile.location?.city || 'Nearby'}
            </Typography>
          </Box>

          {user.profile.occupation && (
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <WorkIcon sx={{ mr: 1, fontSize: '1.2rem' }} />
              <Typography variant="body2">{user.profile.occupation}</Typography>
            </Box>
          )}

          {user.profile.education && (
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <SchoolIcon sx={{ mr: 1, fontSize: '1.2rem' }} />
              <Typography variant="body2">{user.profile.education}</Typography>
            </Box>
          )}

          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
            {user.profile.interests?.map((interest, index) => (
              <Chip
                key={index}
                label={interest}
                size="small"
                sx={{
                  backgroundColor: 'rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  height: '24px',
                  '& .MuiChip-label': {
                    fontSize: '0.75rem',
                    px: 1,
                  },
                }}
              />
            ))}
          </Box>
        </CardOverlay>
      </CardWrapper>

      {action && (
        <>
          <SparkleContainer>
            {stars.map((star) => (
              <Star
                key={star.id}
                delay={star.delay}
                top={star.top}
                left={star.left}
                size={star.size}
                color={star.color}
              />
            ))}
          </SparkleContainer>
          <ActionIndicator type={action}>
            {action === 'like' ? 'LIKE' : action === 'superlike' ? 'SUPER' : 'NOPE'}
          </ActionIndicator>
        </>
      )}

      <ActionButtons>
        <ActionButton 
          color="pass" 
          onClick={() => handleAction('nope')}
          disabled={isAnimating}
          sx={{ opacity: isAnimating ? 0.6 : 1 }}
        >
          <CloseIcon />
        </ActionButton>
        <ActionButton 
          color="superlike" 
          onClick={() => handleAction('superlike')}
          disabled={isAnimating}
          sx={{ opacity: isAnimating ? 0.6 : 1 }}
        >
          <StarIcon />
        </ActionButton>
        <ActionButton 
          color="like" 
          onClick={() => handleAction('like')}
          disabled={isAnimating}
          sx={{ opacity: isAnimating ? 0.6 : 1 }}
        >
          <FavoriteIcon />
        </ActionButton>
      </ActionButtons>
    </Box>
  );
};

export default SwipeCard;
