import React, { useState } from 'react';
import {
  Card,
  CardMedia,
  Typography,
  Box,
  IconButton,
  Chip,
  styled,
} from '@mui/material';
import {
  Close as CloseIcon,
  Favorite as FavoriteIcon,
  Star as StarIcon,
  LocationOn as LocationIcon,
  School as SchoolIcon,
  Work as WorkIcon,
} from '@mui/icons-material';

const CardWrapper = styled(Card)(({ theme }) => ({
  width: '100%',
  height: '520px',
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius * 2,
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
  position: 'relative',
  overflow: 'hidden',
}));

const CardOverlay = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: 0,
  left: 0,
  right: 0,
  background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0.4) 50%, transparent 100%)',
  padding: theme.spacing(2),
  color: 'white',
  borderBottomLeftRadius: theme.shape.borderRadius * 2,
  borderBottomRightRadius: theme.shape.borderRadius * 2,
}));

const ActionButtons = styled(Box)(({ theme }) => ({
  position: 'absolute',
  bottom: '-70px',
  left: 0,
  right: 0,
  display: 'flex',
  justifyContent: 'center',
  gap: theme.spacing(4),
  width: '100%',
  zIndex: 1,
}));

const ActionButton = styled(IconButton)(({ theme, color }) => ({
  backgroundColor: color === 'like' 
    ? theme.palette.success.main 
    : color === 'superlike' 
      ? '#00bcd4' 
      : theme.palette.error.main,
  width: '60px',
  height: '60px',
  boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)',
  '&:hover': {
    backgroundColor: color === 'like' 
      ? theme.palette.success.light 
      : color === 'superlike' 
        ? '#26c6da' 
        : theme.palette.error.light,
    transform: 'scale(1.1)',
  },
  '& svg': {
    color: 'white',
    fontSize: '2rem',
  },
}));

const ActionIndicator = styled(Box)(({ type }) => ({
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%) scale(0)',
  padding: '12px 24px',
  borderRadius: '8px',
  fontSize: '32px',
  fontWeight: 'bold',
  textTransform: 'uppercase',
  border: '4px solid',
  zIndex: 10,
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  animation: 'popIn 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55) forwards',
  '@keyframes popIn': {
    '0%': {
      transform: 'translate(-50%, -50%) scale(0)',
      opacity: 0,
    },
    '50%': {
      transform: 'translate(-50%, -50%) scale(1.2)',
    },
    '100%': {
      transform: 'translate(-50%, -50%) scale(1)',
      opacity: 1,
    }
  },
  ...(type === 'like' ? {
    color: '#4CAF50',
    borderColor: '#4CAF50',
  } : type === 'superlike' ? {
    color: '#00bcd4',
    borderColor: '#00bcd4',
  } : {
    color: '#f44336',
    borderColor: '#f44336',
  }),
}));

const Sparkle = styled(Box)(({ delay, top, left, size }) => ({
  position: 'absolute',
  width: size,
  height: size,
  background: '#fff',
  borderRadius: '50%',
  animation: `sparkle 0.8s ${delay}s ease-in-out`,
  opacity: 0,
  '@keyframes sparkle': {
    '0%': {
      transform: 'scale(0) rotate(0deg)',
      opacity: 0,
    },
    '50%': {
      transform: 'scale(1) rotate(180deg)',
      opacity: 1,
    },
    '100%': {
      transform: 'scale(0) rotate(360deg)',
      opacity: 0,
    }
  },
  top,
  left,
}));

const SparkleContainer = styled(Box)({
  position: 'absolute',
  top: '50%',
  left: '50%',
  width: '200px',
  height: '200px',
  transform: 'translate(-50%, -50%)',
  pointerEvents: 'none',
  zIndex: 9,
});

const PhotoNavigation = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  height: '75%',
  display: 'flex',
});

const PhotoNavArea = styled(Box)({
  flex: 1,
  cursor: 'pointer',
});

const PhotoIndicators = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: theme.spacing(2),
  left: 0,
  right: 0,
  display: 'flex',
  justifyContent: 'center',
  gap: theme.spacing(1),
  padding: theme.spacing(0, 2),
  zIndex: 2,
}));

const PhotoIndicator = styled(Box)(({ active, theme }) => ({
  height: 4,
  flex: 1,
  maxWidth: 60,
  backgroundColor: active ? 'white' : 'rgba(255, 255, 255, 0.5)',
  borderRadius: 2,
  transition: 'background-color 0.3s ease',
}));

const generateSparkles = (count) => {
  const sparkles = [];
  for (let i = 0; i < count; i++) {
    const angle = (360 / count) * i;
    const radius = 80;
    const top = 100 + radius * Math.sin((angle * Math.PI) / 180);
    const left = 100 + radius * Math.cos((angle * Math.PI) / 180);
    sparkles.push({
      id: i,
      delay: i * 0.05,
      top: `${top}px`,
      left: `${left}px`,
      size: `${Math.random() * 4 + 2}px`,
    });
  }
  return sparkles;
};

const SwipeCard = ({ user, onLike, onPass, onSuperLike }) => {
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const [action, setAction] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const sparkles = generateSparkles(12);

  const handleAction = (type) => {
    if (isAnimating) return;
    
    setIsAnimating(true);
    setAction(type);

    // Wait for animations to complete (1.2s total)
    setTimeout(async () => {
      try {
        if (type === 'like') {
          await onLike();
        } else if (type === 'superlike') {
          await onSuperLike();
        } else {
          onPass();
        }
      } catch (error) {
        console.error('Action failed:', error);
      }
      
      // Reset states after action completes
      setAction(null);
      setIsAnimating(false);
    }, 1200); // Increased delay to show animations
  };

  const handlePhotoNavigation = (e) => {
    if (isAnimating) return; // Prevent photo navigation during animation
    
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const width = rect.width;

    if (x < width / 2 && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(prev => prev - 1);
    } else if (x >= width / 2 && currentPhotoIndex < user.profile.photos.length - 1) {
      setCurrentPhotoIndex(prev => prev + 1);
    }
  };

  return (
    <Box sx={{ position: 'relative', width: '100%', height: '100%' }}>
      <CardWrapper 
        sx={{ 
          transition: 'transform 0.3s ease, opacity 0.3s ease',
          transform: action ? 'scale(0.95)' : 'scale(1)',
          opacity: action ? 0.8 : 1,
        }}
      >
        {action && (
          <>
            <SparkleContainer>
              {sparkles.map((sparkle) => (
                <Sparkle
                  key={sparkle.id}
                  delay={sparkle.delay}
                  top={sparkle.top}
                  left={sparkle.left}
                  size={sparkle.size}
                />
              ))}
            </SparkleContainer>
            <ActionIndicator type={action}>
              {action === 'like' ? 'LIKE' : action === 'superlike' ? 'SUPER' : 'NOPE'}
            </ActionIndicator>
          </>
        )}

        <CardMedia
          component="img"
          height="100%"
          image={user.profile.photos[currentPhotoIndex]}
          alt={`${user.profile.firstName}'s photo`}
          sx={{ objectFit: 'cover' }}
        />

        <PhotoNavigation>
          <PhotoNavArea onClick={handlePhotoNavigation} />
        </PhotoNavigation>

        <PhotoIndicators>
          {user.profile.photos.map((_, index) => (
            <PhotoIndicator
              key={index}
              active={index === currentPhotoIndex}
            />
          ))}
        </PhotoIndicators>

        <CardOverlay>
          <Typography variant="h5" component="div" sx={{ mb: 1 }}>
            {user.profile.firstName}, {user.profile.age}
          </Typography>
          
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <LocationIcon sx={{ mr: 1, fontSize: '1.2rem' }} />
            <Typography variant="body2">
              {user.profile.location?.city || 'Nearby'}
            </Typography>
          </Box>

          {user.profile.occupation && (
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <WorkIcon sx={{ mr: 1, fontSize: '1.2rem' }} />
              <Typography variant="body2">{user.profile.occupation}</Typography>
            </Box>
          )}

          {user.profile.education && (
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <SchoolIcon sx={{ mr: 1, fontSize: '1.2rem' }} />
              <Typography variant="body2">{user.profile.education}</Typography>
            </Box>
          )}

          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
            {user.profile.interests?.map((interest, index) => (
              <Chip
                key={index}
                label={interest}
                size="small"
                sx={{
                  backgroundColor: 'rgba(255, 255, 255, 0.2)',
                  color: 'white',
                  height: '24px',
                  '& .MuiChip-label': {
                    fontSize: '0.75rem',
                    px: 1,
                  },
                }}
              />
            ))}
          </Box>
        </CardOverlay>
      </CardWrapper>

      <ActionButtons>
        <ActionButton 
          color="pass" 
          onClick={() => handleAction('nope')}
          disabled={isAnimating}
          sx={{ opacity: isAnimating ? 0.6 : 1 }}
        >
          <CloseIcon />
        </ActionButton>
        <ActionButton 
          color="superlike" 
          onClick={() => handleAction('superlike')}
          disabled={isAnimating}
          sx={{ opacity: isAnimating ? 0.6 : 1 }}
        >
          <StarIcon />
        </ActionButton>
        <ActionButton 
          color="like" 
          onClick={() => handleAction('like')}
          disabled={isAnimating}
          sx={{ opacity: isAnimating ? 0.6 : 1 }}
        >
          <FavoriteIcon />
        </ActionButton>
      </ActionButtons>
    </Box>
  );
};

export default SwipeCard;
