import React, { useState, useRef, useEffect } from 'react';
import { Box, Typography, Chip } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Close, Favorite, Star } from '@mui/icons-material';

const CardContainer = styled(Box)({
  position: 'relative',
  width: '100%',
  height: '100%',
  perspective: '1000px',
  transformStyle: 'preserve-3d',
});

const CardWrapper = styled(Box)(({ isDragging, isActive, showDetails }) => ({
  position: 'absolute',
  width: '100%',
  height: showDetails ? '85vh' : '100%',
  cursor: isActive && !isDragging ? 'grab' : isDragging ? 'grabbing' : 'default',
  transition: isDragging ? 'none' : 'all 0.3s cubic-bezier(0.34, 1.56, 0.64, 1)',
  transformStyle: 'preserve-3d',
  borderRadius: '10px',
  boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
  willChange: 'transform',
  pointerEvents: isActive ? 'auto' : 'none',
  backgroundColor: 'white',
  overflow: 'hidden',
}));

const ProfileImage = styled('img')({
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  transition: 'all 0.3s ease',
});

const PhotoNavigation = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  height: '100%',
  display: 'flex',
  '& > *': {
    flex: 1,
    cursor: 'pointer',
    background: 'transparent',
    border: 'none',
    outline: 'none',
    transition: 'background-color 0.2s',
    '&:hover': {
      background: 'linear-gradient(to right, rgba(0,0,0,0.1), transparent, transparent)',
      '&:last-child': {
        background: 'linear-gradient(to left, rgba(0,0,0,0.1), transparent, transparent)',
      }
    }
  }
});

const ProfileInfo = styled(Box)(({ showDetails }) => ({
  position: 'absolute',
  bottom: 0,
  left: 0,
  right: 0,
  padding: '20px',
  background: 'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.5) 60%, transparent 100%)',
  color: 'white',
  transition: 'all 0.3s cubic-bezier(0.34, 1.56, 0.64, 1)',
  transform: showDetails ? 'translateY(0)' : 'translateY(70%)',
  height: showDetails ? '100%' : '180px',
  overflow: 'auto',
  '& .MuiTypography-root': {
    color: 'white',
  },
  '&::-webkit-scrollbar': {
    width: '4px',
  },
  '&::-webkit-scrollbar-track': {
    background: 'rgba(255, 255, 255, 0.1)',
  },
  '&::-webkit-scrollbar-thumb': {
    background: 'rgba(255, 255, 255, 0.3)',
    borderRadius: '2px',
  }
}));

const PhotoIndicators = styled(Box)({
  position: 'absolute',
  top: '10px',
  left: '0',
  right: '0',
  display: 'flex',
  justifyContent: 'center',
  gap: '4px',
  padding: '0 10px',
  zIndex: 2,
});

const PhotoIndicator = styled(Box)(({ active }) => ({
  height: '3px',
  flex: 1,
  maxWidth: '60px',
  backgroundColor: active ? 'white' : 'rgba(255, 255, 255, 0.5)',
  borderRadius: '2px',
  transition: 'all 0.3s ease',
}));

const InterestTags = styled(Box)({
  display: 'flex',
  flexWrap: 'wrap',
  gap: '8px',
  marginTop: '8px',
  '& .MuiChip-root': {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    color: 'white',
    '&:hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.3)',
    }
  }
});

const ActionButtons = styled(Box)({
  position: 'absolute',
  bottom: '20px',
  left: '50%',
  transform: 'translateX(-50%)',
  display: 'flex',
  gap: '20px',
  zIndex: 1000,
});

const ActionButton = styled(Box)(({ color = 'primary' }) => ({
  width: '60px',
  height: '60px',
  borderRadius: '50%',
  backgroundColor: 'white',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  cursor: 'pointer',
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
  transition: 'all 0.2s ease',
  '& svg': {
    fontSize: '28px',
    color: color === 'error' ? '#ff4b4b' : 
           color === 'success' ? '#4caf50' : 
           '#2196f3',
  },
  '&:hover': {
    transform: 'scale(1.1)',
    backgroundColor: color === 'error' ? '#ff4b4b' : 
                    color === 'success' ? '#4caf50' : 
                    '#2196f3',
    '& svg': {
      color: 'white',
    }
  }
}));

const ActionIndicator = styled(Box)(({ type }) => ({
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  padding: '8px 16px',
  borderRadius: '4px',
  backgroundColor: type === 'like' ? '#4caf50' :
                  type === 'nope' ? '#ff4b4b' :
                  '#2196f3',
  color: 'white',
  fontWeight: 'bold',
  fontSize: '24px',
  zIndex: 1000,
  pointerEvents: 'none',
}));

const SparkleContainer = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  pointerEvents: 'none',
  zIndex: 1000,
});

const SparkleEffect = styled(Box)(({ delay, size, color }) => ({
  position: 'absolute',
  width: size,
  height: size,
  backgroundColor: color,
  borderRadius: '50%',
  animation: `sparkle 1s ease-in-out ${delay}s`,
  '@keyframes sparkle': {
    '0%': {
      transform: 'scale(0) rotate(0deg)',
      opacity: 0,
    },
    '50%': {
      transform: 'scale(1) rotate(180deg)',
      opacity: 1,
    },
    '100%': {
      transform: 'scale(0) rotate(360deg)',
      opacity: 0,
    },
  },
}));

const SwipeCard = ({ user, onLike, onPass, onSuperLike, isActive }) => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [action, setAction] = useState(null);
  const [stars, setStars] = useState([]);
  const [showDetails, setShowDetails] = useState(false);
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const cardRef = useRef(null);
  const startPos = useRef({ x: 0, y: 0 });
  const currentPos = useRef({ x: 0, y: 0 });

  useEffect(() => {
    if (action) {
      const generateStars = () => {
        const newStars = [];
        const count = 12;
        const colors = action === 'like' ? ['#ff6b6b', '#ff8787', '#ffa8a8'] :
                      action === 'superlike' ? ['#339af0', '#4dabf7', '#74c0fc'] :
                      ['#868e96', '#adb5bd', '#ced4da'];

        for (let i = 0; i < count; i++) {
          newStars.push({
            id: i,
            top: Math.random() * 100 + '%',
            left: Math.random() * 100 + '%',
            size: Math.random() * 8 + 4,
            delay: Math.random() * 0.2,
            color: colors[Math.floor(Math.random() * colors.length)],
          });
        }
        setStars(newStars);
      };
      generateStars();
    }
  }, [action]);

  const handlePhotoNavigation = (e) => {
    e.stopPropagation();
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const width = rect.width;
    const clickPosition = x / width;

    if (clickPosition < 0.3 && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(prev => prev - 1);
    } else if (clickPosition > 0.7 && currentPhotoIndex < (user?.profile?.photos?.length ?? 0) - 1) {
      setCurrentPhotoIndex(prev => prev + 1);
    }
  };

  const handleMouseDown = (e) => {
    if (!isActive || isAnimating) return;
    setIsDragging(true);
    const rect = cardRef.current.getBoundingClientRect();
    startPos.current = {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    };
    currentPos.current = { x: 0, y: 0 };
  };

  const handleMouseMove = (e) => {
    if (!isActive || !isDragging || isAnimating) return;
    const rect = cardRef.current.getBoundingClientRect();
    const deltaX = e.clientX - rect.left - startPos.current.x;
    const deltaY = e.clientY - rect.top - startPos.current.y;
    currentPos.current = { x: deltaX, y: deltaY };
    setPosition({ x: deltaX, y: deltaY });

    const rotation = deltaX * 0.1;
    cardRef.current.style.transform = `
      translate(${deltaX}px, ${deltaY}px)
      rotate(${rotation}deg)
    `;

    if (deltaX > 100) {
      setAction('like');
    } else if (deltaX < -100) {
      setAction('nope');
    } else {
      setAction(null);
    }
  };

  const handleSwipeEnd = () => {
    if (!isActive || !isDragging || isAnimating) return;
    setIsDragging(false);
    setIsAnimating(true);

    const { x } = currentPos.current;
    if (Math.abs(x) > 100) {
      const direction = x > 0 ? 'right' : 'left';
      const rotation = x > 0 ? 30 : -30;
      
      cardRef.current.style.transition = 'transform 0.3s ease';
      cardRef.current.style.transform = `
        translate(${direction === 'right' ? window.innerWidth + 200 : -window.innerWidth - 200}px, 0)
        rotate(${rotation}deg)
      `;

      if (direction === 'right') {
        onLike?.();
      } else {
        onPass?.();
      }

      setTimeout(() => {
        cardRef.current.style.transition = 'none';
        cardRef.current.style.transform = 'none';
        setPosition({ x: 0, y: 0 });
        setAction(null);
        setIsAnimating(false);
      }, 300);
    } else {
      cardRef.current.style.transition = 'transform 0.3s ease';
      cardRef.current.style.transform = 'none';
      setPosition({ x: 0, y: 0 });
      setAction(null);
      
      setTimeout(() => {
        setIsAnimating(false);
      }, 300);
    }
  };

  const handleActionClick = (actionType) => {
    if (!isActive || isAnimating) return;
    setIsAnimating(true);
    setAction(actionType);

    const direction = actionType === 'like' ? 'right' : actionType === 'nope' ? 'left' : 'up';
    const rotation = direction === 'right' ? 30 : direction === 'left' ? -30 : 0;
    const x = direction === 'right' ? window.innerWidth + 200 : direction === 'left' ? -window.innerWidth - 200 : 0;
    const y = direction === 'up' ? -window.innerHeight - 200 : 0;

    cardRef.current.style.transition = 'transform 0.3s ease';
    cardRef.current.style.transform = `
      translate(${x}px, ${y}px)
      rotate(${rotation}deg)
    `;

    if (actionType === 'like') onLike?.();
    else if (actionType === 'superlike') onSuperLike?.();
    else onPass?.();

    setTimeout(() => {
      cardRef.current.style.transition = 'none';
      cardRef.current.style.transform = 'none';
      setPosition({ x: 0, y: 0 });
      setAction(null);
      setIsAnimating(false);
    }, 300);
  };

  const handleProfileClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const clickY = e.clientY - rect.top;
    if (clickY > rect.height * 0.7 || showDetails) {
      setShowDetails(!showDetails);
    }
  };

  if (!user || !user.profile) {
    return (
      <CardContainer>
        <CardWrapper sx={{ justifyContent: 'center', alignItems: 'center', display: 'flex' }}>
          <Typography variant="h6" color="text.secondary">
            No profiles available
          </Typography>
        </CardWrapper>
      </CardContainer>
    );
  }

  const profile = user.profile;

  return (
    <CardContainer>
      <CardWrapper
        ref={cardRef}
        isDragging={isDragging}
        isActive={isActive}
        showDetails={showDetails}
        onClick={handleProfileClick}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleSwipeEnd}
        onMouseLeave={handleSwipeEnd}
      >
        <ProfileImage
          src={profile.photos[currentPhotoIndex]}
          alt={`${profile.firstName}'s photo`}
          style={{
            filter: showDetails ? 'brightness(0.3)' : 'none',
            transform: showDetails ? 'scale(1.1)' : 'none'
          }}
        />

        <PhotoIndicators>
          {profile.photos.map((_, index) => (
            <PhotoIndicator key={index} active={index === currentPhotoIndex} />
          ))}
        </PhotoIndicators>
        
        <PhotoNavigation>
          <Box onClick={handlePhotoNavigation} />
          <Box onClick={handlePhotoNavigation} />
          <Box onClick={handlePhotoNavigation} />
        </PhotoNavigation>

        <ProfileInfo showDetails={showDetails}>
          <Typography variant="h5" component="h2" sx={{ 
            color: 'white', 
            fontWeight: 'bold',
            fontSize: showDetails ? '2rem' : '1.5rem',
            transition: 'all 0.3s ease'
          }}>
            {profile.firstName}, {profile.age}
          </Typography>
          <Typography variant="body1" sx={{ 
            color: 'white', 
            opacity: 0.9,
            marginBottom: showDetails ? '20px' : '0'
          }}>
            {profile.location.city}
          </Typography>
          {showDetails && (
            <ProfileDetails>
              <Typography variant="body1" sx={{ 
                color: 'white',
                fontSize: '1.1rem',
                lineHeight: 1.6,
                marginBottom: '24px'
              }}>
                {profile.bio}
              </Typography>
              <Box mt={3}>
                <Typography variant="subtitle1" sx={{ color: 'white', fontWeight: 'bold', marginBottom: '12px' }}>
                  Interests
                </Typography>
                <InterestTags>
                  {profile.interests.map((interest, index) => (
                    <Chip 
                      key={index} 
                      label={interest}
                      sx={{
                        backgroundColor: 'rgba(255, 255, 255, 0.2)',
                        color: 'white',
                        '&:hover': {
                          backgroundColor: 'rgba(255, 255, 255, 0.3)',
                        }
                      }}
                    />
                  ))}
                </InterestTags>
              </Box>
              {profile.occupation && (
                <Box mt={3}>
                  <Typography variant="subtitle1" sx={{ color: 'white', fontWeight: 'bold', marginBottom: '8px' }}>
                    Occupation
                  </Typography>
                  <Typography variant="body1" sx={{ color: 'white' }}>
                    {profile.occupation}
                  </Typography>
                </Box>
              )}
              {profile.education && (
                <Box mt={3}>
                  <Typography variant="subtitle1" sx={{ color: 'white', fontWeight: 'bold', marginBottom: '8px' }}>
                    Education
                  </Typography>
                  <Typography variant="body1" sx={{ color: 'white' }}>
                    {profile.education}
                  </Typography>
                </Box>
              )}
            </ProfileDetails>
          )}
        </ProfileInfo>

        {action && (
          <>
            <SparkleContainer>
              {stars.map((star) => (
                <SparkleEffect
                  key={star.id}
                  sx={{
                    top: star.top,
                    left: star.left,
                  }}
                  delay={star.delay}
                  size={star.size}
                  color={star.color}
                />
              ))}
            </SparkleContainer>
            <ActionIndicator type={action}>
              {action === 'like' ? 'LIKE' : action === 'superlike' ? 'SUPER' : 'NOPE'}
            </ActionIndicator>
          </>
        )}
      </CardWrapper>
      
      <ActionButtons>
        <ActionButton
          color="error"
          disabled={isAnimating}
          onClick={() => handleActionClick('nope')}
        >
          <Close />
        </ActionButton>
        <ActionButton
          color="info"
          disabled={isAnimating}
          onClick={() => handleActionClick('superlike')}
        >
          <Star />
        </ActionButton>
        <ActionButton
          color="success"
          disabled={isAnimating}
          onClick={() => handleActionClick('like')}
        >
          <Favorite />
        </ActionButton>
      </ActionButtons>
    </CardContainer>
  );
};

SwipeCard.propTypes = {
  user: PropTypes.shape({
    _id: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
    profile: PropTypes.shape({
      firstName: PropTypes.string.isRequired,
      age: PropTypes.number.isRequired,
      location: PropTypes.shape({
        city: PropTypes.string.isRequired,
      }).isRequired,
      photos: PropTypes.arrayOf(PropTypes.string).isRequired,
      bio: PropTypes.string,
      interests: PropTypes.arrayOf(PropTypes.string),
      occupation: PropTypes.string,
      education: PropTypes.string,
    }).isRequired,
  }),
  onLike: PropTypes.func.isRequired,
  onPass: PropTypes.func.isRequired,
  onSuperLike: PropTypes.func,
  isActive: PropTypes.bool,
};

SwipeCard.defaultProps = {
  onLike: () => {},
  onPass: () => {},
  onSuperLike: () => {},
  isActive: false,
};

export default SwipeCard;
