import React, { useState, useRef, useEffect } from 'react';
import { Box, Typography, Chip } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Close, Favorite, Star, ArrowBackIosNew, ArrowForwardIos } from '@mui/icons-material';

const CardContainer = styled(Box)({
  position: 'relative',
  width: '100%',
  maxWidth: '400px',
  height: '600px',
  margin: '0 auto',
  perspective: '1000px',
});

const CardWrapper = styled(Box)(({ isDragging }) => ({
  position: 'absolute',
  width: '100%',
  height: '100%',
  cursor: isDragging ? 'grabbing' : 'grab',
  transition: isDragging ? 'none' : 'all 0.3s ease',
  transformStyle: 'preserve-3d',
  borderRadius: '10px',
  boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
}));

const ProfileImage = styled('img')({
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  borderRadius: '10px',
});

const NavigationButton = styled(Box)(({ theme }) => ({
  position: 'absolute',
  top: '50%',
  transform: 'translateY(-50%)',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  color: 'white',
  padding: '8px',
  borderRadius: '50%',
  cursor: 'pointer',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  transition: 'background-color 0.2s',
  '&:hover': {
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
  },
  '&:first-of-type': {
    left: '10px',
  },
  '&:last-of-type': {
    right: '10px',
  },
  '&[disabled]': {
    opacity: 0.5,
    cursor: 'not-allowed',
  },
}));

const ProfileInfo = styled(Box)(({ showDetails }) => ({
  position: 'absolute',
  bottom: 0,
  left: 0,
  right: 0,
  padding: '20px',
  background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0.4) 60%, transparent 100%)',
  color: 'white',
  borderRadius: '0 0 10px 10px',
  transition: 'height 0.3s ease',
  height: showDetails ? 'auto' : '100px',
  maxHeight: showDetails ? '70%' : '100px',
  overflow: 'hidden',
}));

const InterestTags = styled(Box)({
  display: 'flex',
  flexWrap: 'wrap',
  gap: '8px',
  marginTop: '8px',
  '& .MuiChip-root': {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    color: 'white',
    '&:hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.3)',
    },
  },
});

const ActionButtons = styled(Box)({
  position: 'absolute',
  bottom: '-80px',
  left: '50%',
  transform: 'translateX(-50%)',
  display: 'flex',
  gap: '20px',
  zIndex: 2,
});

const ActionButton = styled(Box)(({ color }) => ({
  width: '60px',
  height: '60px',
  borderRadius: '50%',
  backgroundColor: color === 'error' ? '#ff4b4b' :
                  color === 'success' ? '#4caf50' :
                  color === 'info' ? '#2196f3' : 'white',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  cursor: 'pointer',
  transition: 'transform 0.2s, background-color 0.2s',
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
  '& svg': {
    color: 'white',
    fontSize: '28px',
  },
  '&:hover': {
    transform: 'scale(1.1)',
  },
  '&:active': {
    transform: 'scale(0.95)',
  },
}));

const ActionIndicator = styled(Box)(({ type }) => ({
  position: 'absolute',
  top: '20px',
  padding: '8px 16px',
  borderRadius: '4px',
  color: 'white',
  fontWeight: 'bold',
  transform: 'rotate(-20deg)',
  zIndex: 3,
  ...(type === 'like' && {
    right: '20px',
    backgroundColor: '#4caf50',
    border: '3px solid #4caf50',
  }),
  ...(type === 'nope' && {
    left: '20px',
    backgroundColor: '#ff4b4b',
    border: '3px solid #ff4b4b',
  }),
  ...(type === 'superlike' && {
    right: '20px',
    backgroundColor: '#2196f3',
    border: '3px solid #2196f3',
  }),
}));

const SparkleContainer = styled(Box)({
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  pointerEvents: 'none',
  zIndex: 2,
});

const SparkleEffect = styled(Box)(({ delay, size, color }) => ({
  position: 'absolute',
  width: size + 'px',
  height: size + 'px',
  backgroundColor: color,
  borderRadius: '50%',
  animation: `sparkle 0.5s linear ${delay}s forwards`,
  opacity: 0,
  '@keyframes sparkle': {
    '0%': {
      transform: 'scale(0) rotate(0deg)',
      opacity: 0,
    },
    '50%': {
      opacity: 1,
    },
    '100%': {
      transform: 'scale(1) rotate(180deg)',
      opacity: 0,
    },
  },
}));

const SwipeCard = ({ profile, onSwipe }) => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [action, setAction] = useState(null);
  const [stars, setStars] = useState([]);
  const [showDetails, setShowDetails] = useState(false);
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const cardRef = useRef(null);
  const startPos = useRef({ x: 0, y: 0 });
  const currentPos = useRef({ x: 0, y: 0 });

  const handlePhotoNavigation = (e) => {
    e.stopPropagation();
    const direction = e.currentTarget.dataset.direction;
    if (direction === 'prev' && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(prev => prev - 1);
    } else if (direction === 'next' && currentPhotoIndex < profile.photos.length - 1) {
      setCurrentPhotoIndex(prev => prev + 1);
    }
  };

  const handleMouseDown = (e) => {
    if (isAnimating) return;
    setIsDragging(true);
    const rect = cardRef.current.getBoundingClientRect();
    startPos.current = {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    };
    currentPos.current = { x: 0, y: 0 };
  };

  const handleMouseMove = (e) => {
    if (!isDragging || isAnimating) return;
    const rect = cardRef.current.getBoundingClientRect();
    const deltaX = e.clientX - rect.left - startPos.current.x;
    const deltaY = e.clientY - rect.top - startPos.current.y;
    currentPos.current = { x: deltaX, y: deltaY };
    setPosition({ x: deltaX, y: deltaY });

    if (deltaX > 100) {
      setAction('like');
    } else if (deltaX < -100) {
      setAction('nope');
    } else {
      setAction(null);
    }
  };

  const handleSwipeEnd = () => {
    if (!isDragging || isAnimating) return;
    setIsDragging(false);
    setIsAnimating(true);

    const { x } = currentPos.current;
    if (Math.abs(x) > 100) {
      const direction = x > 0 ? 'right' : 'left';
      const action = x > 0 ? 'like' : 'nope';
      onSwipe(action);
      
      setPosition(prev => ({
        x: direction === 'right' ? window.innerWidth : -window.innerWidth,
        y: prev.y
      }));
    } else {
      setPosition({ x: 0, y: 0 });
      setAction(null);
    }
    
    setIsAnimating(false);
  };

  const handleActionClick = (actionType) => {
    if (isAnimating) return;
    setIsAnimating(true);
    setAction(actionType);

    const direction = actionType === 'like' ? 'right' : actionType === 'nope' ? 'left' : 'up';
    const x = direction === 'right' ? window.innerWidth : direction === 'left' ? -window.innerWidth : 0;
    const y = direction === 'up' ? -window.innerHeight : 0;

    setPosition({ x, y });
    onSwipe(actionType);
    
    setIsAnimating(false);
  };

  const handleProfileClick = () => {
    if (!isDragging) {
      setShowDetails(!showDetails);
    }
  };

  useEffect(() => {
    if (action) {
      const generateStars = () => {
        const newStars = [];
        const count = 12;
        const colors = action === 'like' ? ['#ff6b6b', '#ff8787', '#ffa8a8'] :
                      action === 'superlike' ? ['#339af0', '#4dabf7', '#74c0fc'] :
                      ['#868e96', '#adb5bd', '#ced4da'];

        for (let i = 0; i < count; i++) {
          newStars.push({
            id: i,
            top: Math.random() * 100 + '%',
            left: Math.random() * 100 + '%',
            size: Math.random() * 8 + 4,
            delay: Math.random() * 0.2,
            color: colors[Math.floor(Math.random() * colors.length)],
          });
        }
        setStars(newStars);
      };
      generateStars();
    }
  }, [action]);

  return (
    <CardContainer>
      <CardWrapper
        ref={cardRef}
        isDragging={isDragging}
        onClick={handleProfileClick}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleSwipeEnd}
        onMouseLeave={handleSwipeEnd}
        sx={{
          transform: `
            translate(${position.x}px, ${position.y}px) 
            rotate(${position.x * 0.1}deg)
            scale(${action ? 0.95 : 1})
          `,
          opacity: action ? 0.8 : 1,
        }}
      >
        <ProfileImage
          src={profile.photos[currentPhotoIndex]}
          alt={`${profile.name}'s photo`}
        />
        
        <PhotoNavigation>
          <NavigationButton
            data-direction="prev"
            onClick={handlePhotoNavigation}
            disabled={currentPhotoIndex === 0}
          >
            <ArrowBackIosNew />
          </NavigationButton>
          <NavigationButton
            data-direction="next"
            onClick={handlePhotoNavigation}
            disabled={currentPhotoIndex === profile.photos.length - 1}
          >
            <ArrowForwardIos />
          </NavigationButton>
        </PhotoNavigation>

        <ProfileInfo showDetails={showDetails}>
          <Typography variant="h5" component="h2">
            {profile.name}, {profile.age}
          </Typography>
          <Typography variant="body1" color="text.secondary">
            {profile.location}
          </Typography>
          {showDetails && (
            <ProfileDetails>
              <Typography variant="body1">
                {profile.bio}
              </Typography>
              <Box mt={2}>
                <Typography variant="subtitle1" fontWeight="bold">
                  Interests
                </Typography>
                <InterestTags>
                  {profile.interests.map((interest, index) => (
                    <Chip key={index} label={interest} />
                  ))}
                </InterestTags>
              </Box>
              {profile.occupation && (
                <Box mt={2}>
                  <Typography variant="subtitle1" fontWeight="bold">
                    Occupation
                  </Typography>
                  <Typography variant="body1">
                    {profile.occupation}
                  </Typography>
                </Box>
              )}
              {profile.education && (
                <Box mt={2}>
                  <Typography variant="subtitle1" fontWeight="bold">
                    Education
                  </Typography>
                  <Typography variant="body1">
                    {profile.education}
                  </Typography>
                </Box>
              )}
            </ProfileDetails>
          )}
        </ProfileInfo>

        {action && (
          <>
            <SparkleContainer>
              {stars.map((star) => (
                <SparkleEffect
                  key={star.id}
                  sx={{
                    top: star.top,
                    left: star.left,
                  }}
                  delay={star.delay}
                  size={star.size}
                  color={star.color}
                />
              ))}
            </SparkleContainer>
            <ActionIndicator type={action}>
              {action === 'like' ? 'LIKE' : action === 'superlike' ? 'SUPER' : 'NOPE'}
            </ActionIndicator>
          </>
        )}
      </CardWrapper>
      
      <ActionButtons>
        <ActionButton
          color="error"
          disabled={isAnimating}
          onClick={() => handleActionClick('nope')}
        >
          <Close />
        </ActionButton>
        <ActionButton
          color="info"
          disabled={isAnimating}
          onClick={() => handleActionClick('superlike')}
        >
          <Star />
        </ActionButton>
        <ActionButton
          color="success"
          disabled={isAnimating}
          onClick={() => handleActionClick('like')}
        >
          <Favorite />
        </ActionButton>
      </ActionButtons>
    </CardContainer>
  );
};

export default SwipeCard;
